const handleSubmit = async (event) => {
    event.preventDefault();
    console.log(workoutData); // Verifique se o workoutData está estruturado corretamente

    try {
        const response = await fetch('http://localhost:5000/api/workouts', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(workoutData), // Certifique-se de que workoutData é o que você espera
        });

        if (!response.ok) {
            const errorResponse = await response.json();
            throw new Error(errorResponse.error || 'Erro ao cadastrar treino.'); // Melhora a mensagem de erro
        }

        const result = await response.json();
        console.log('Treino cadastrado com sucesso:', result); // Para verificar o resultado
    } catch (error) {
        console.error('Erro ao cadastrar treino:', error.message); // Exibe o erro
        alert('Erro ao cadastrar treino. Verifique os dados e tente novamente.');
    }
};
