import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom'; // Importando useParams para pegar o ID da URL

const WorkoutListPage = () => {
  const [workouts, setWorkouts] = useState([]);
  const [loading, setLoading] = useState(true);
  const { id } = useParams(); // Pegando o 'id' da URL

  useEffect(() => {
    const fetchWorkouts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/workouts');
        console.log('Workouts recebidos:', response.data); // Verificando os dados
        setWorkouts(response.data);
      } catch (error) {
        console.error('Erro ao carregar os workouts', error);
      } finally {
        setLoading(false);
      }
    };

    fetchWorkouts();
  }, []); // Dependência vazia para carregar apenas uma vez

  if (loading) {
    return <div>Carregando...</div>;
  }

  // Filtra os treinos do aluno específico
  const filteredWorkouts = id
    ? workouts.filter((workout) => workout.aluno_id === parseInt(id))
    : workouts;  // Se id não for fornecido, exibe todos os treinos

  if (id && filteredWorkouts.length === 0) {
    return <div>Treino não encontrado para o aluno com ID {id}</div>;
  }

  return (
    <div>
      <h1>Lista de Workouts</h1>

      {/* Exibe os treinos filtrados */}
      <ul>
        {filteredWorkouts.map((workout) => (
          <li key={workout.id}>
            <h2>{workout.name}</h2>
            <p>{workout.description}</p>
            <p>Trainer ID: {workout.trainer_id}</p>
            <p>Duração: {workout.duration} minutos</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default WorkoutListPage;
