import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, Button, Spinner, Alert, Modal } from 'react-bootstrap';

const WorkoutListPage = () => {
  const [workouts, setWorkouts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showUsersModal, setShowUsersModal] = useState(false); // Controle do modal de usuários
  const [users, setUsers] = useState([]); // Lista de usuários
  const { id } = useParams(); // Pegando o 'id' da URL (que é o user_id)

  useEffect(() => {
    const fetchWorkouts = async () => {
      if (!id) {
        console.error('ID não fornecido na URL');
        return;
      }

      const userId = parseInt(id, 10);

      if (isNaN(userId)) {
        console.error('ID inválido');
        return;
      }

      try {
        console.log('Fazendo requisição para treinos...');
        const response = await axios.get(`http://localhost:5000/api/workouts/${userId}`);
        console.log('Resposta recebida dos treinos:', response.data);

        if (response.data.length === 0) {
          console.log('Nenhum treino encontrado.');
        } else {
          const workoutsWithTrainerName = await Promise.all(
            response.data.map(async (workout) => {
              try {
                // Buscando o nome do treinador para cada treino
                const trainerResponse = await axios.get(`http://localhost:5000/api/trainers/${workout.trainer_id}`);
                const trainerName = trainerResponse.data.name;  // Ajuste o campo conforme o formato da resposta da API
                return {
                  ...workout,
                  trainer_name: trainerName || 'Nome do treinador não encontrado',
                };
              } catch (error) {
                console.error('Erro ao buscar o nome do treinador', error);
                return {
                  ...workout,
                  trainer_name: 'Nome do treinador não encontrado',
                };
              }
            })
          );

          setWorkouts(workoutsWithTrainerName);
        }
      } catch (error) {
        console.error('Erro ao carregar os workouts', error);
        setError('Erro ao carregar os treinos. Tente novamente mais tarde.');
      } finally {
        setLoading(false);
      }
    };

    fetchWorkouts();
  }, [id]); // Dependência do id

  // Função para buscar os usuários vinculados ao treinador
  const fetchUsersForTrainer = async (trainerId) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/trainers/${trainerId}/users`);
      setUsers(response.data); // Atualiza a lista de usuários
      setShowUsersModal(true); // Exibe o modal de usuários
    } catch (error) {
      console.error('Erro ao buscar usuários do treinador', error);
    }
  };

  // Fechar o modal de usuários
  const handleCloseModal = () => {
    setShowUsersModal(false);
  };

  if (loading) {
    return (
      <div className="text-center">
        <Spinner animation="border" variant="success" />
        <p>Carregando...</p>
      </div>
    );
  }

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Lista de Treinos</h1>

      {error && (
        <Alert variant="danger">
          {error}
        </Alert>
      )}

      {workouts.length === 0 ? (
        <Alert variant="info" className="text-center">
          Nenhum treino encontrado para o usuário com ID {id}.
        </Alert>
      ) : (
        <div className="row">
          {workouts.map((workout) => (
            <div className="col-md-4 mb-4" key={workout.id}>
              <Card>
                <Card.Body>
                  <Card.Title>{workout.name}</Card.Title>
                  <Card.Subtitle className="mb-2 text-muted">Duração: {workout.duration} minutos</Card.Subtitle>
                  <Card.Text>{workout.description}</Card.Text>
                  <Card.Text>
                    <strong>Treinador:</strong> {workout.trainer_id} - {workout.trainer_name}
                  </Card.Text>
                  <Button variant="primary" onClick={() => alert('Visualizando mais detalhes do treino!')}>
                    Ver Detalhes
                  </Button>
                  {/* Botão para abrir o modal com os usuários do treinador */}
                  <Button 
                    variant="secondary" 
                    onClick={() => fetchUsersForTrainer(workout.trainer_id)}>
                    Ver Usuários
                  </Button>
                </Card.Body>
              </Card>
            </div>
          ))}
        </div>
      )}

      {/* Modal de Usuários */}
      <Modal show={showUsersModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Usuários Vinculados ao Treinador</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {users.length > 0 ? (
            <ul>
              {users.map((user) => (
                <li key={user.id}>
                  <Button variant="link" onClick={() => alert(`Visualizando treino de ${user.name}`)}>
                    {user.name}
                  </Button>
                </li>
              ))}
            </ul>
          ) : (
            <p>Não há usuários vinculados a este treinador.</p>
          )}
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default WorkoutListPage;
