import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const AcompanharTreinoPage = () => {
    const { alunoId } = useParams();
    const [workout, setWorkout] = useState(null);

    useEffect(() => {
        axios.get(`http://localhost:5000/api/workouts/aluno/${alunoId}`)
            .then(response => {
                setWorkout(response.data);
            })
            .catch(error => {
                console.error('Erro ao carregar treino:', error);
            });
    }, [alunoId]);

    if (!workout) return <div>Carregando treino...</div>;

    return (
        <div className="container mt-5">
            <h2 className="text-center text-primary">Acompanhamento de Treino</h2>
            <h3>{workout.name}</h3>
            <p>{workout.description}</p>
            <p>Duração: {workout.duration} minutos</p>

            <h4>Exercícios</h4>
            {workout.exercises.map((exercise, index) => (
                <div key={index} className="exercise-detail mb-3">
                    <p><strong>Nome:</strong> {exercise.name}</p>
                    <p><strong>Séries:</strong> {exercise.sets}</p>
                    <p><strong>Repetições:</strong> {exercise.reps}</p>
                    <p><strong>Peso:</strong> {exercise.weight} kg</p>
                </div>
            ))}
        </div>
    );
};

export default AcompanharTreinoPage;
