import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Alert } from 'react-bootstrap';

const TreinoCadastroPage = () => {
    const [workoutData, setWorkoutData] = useState({ name: '', description: '' });
    const [feedback, setFeedback] = useState({ type: '', message: '' });
    const navigate = useNavigate();

    const handleChange = (e) => {
        setWorkoutData({ ...workoutData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:5000/api/workouts', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(workoutData),
            });

            if (response.ok) {
                setFeedback({ type: 'success', message: 'Treino cadastrado com sucesso!' });
                setTimeout(() => {
                    navigate('/trainers'); // Redireciona para a lista de treinadores após o cadastro
                }, 2000);
            } else {
                setFeedback({ type: 'danger', message: 'Erro ao cadastrar treino. Verifique os dados e tente novamente.' });
            }
        } catch (error) {
            console.error(error);
            setFeedback({ type: 'danger', message: 'Erro ao cadastrar treino. Tente novamente mais tarde.' });
        }
    };

    return (
        <div className="container mt-5">
            <h1 className="text-center text-success">Cadastro de Treino</h1>

            {feedback.message && (
                <Alert variant={feedback.type} onClose={() => setFeedback({ type: '', message: '' })} dismissible>
                    {feedback.message}
                </Alert>
            )}

            <form onSubmit={handleSubmit} className="form-group">
                <input
                    type="text"
                    name="name"
                    placeholder="Nome do Treino"
                    className="form-control mb-3"
                    value={workoutData.name}
                    onChange={handleChange}
                    required
                />
                <textarea
                    name="description"
                    placeholder="Descrição"
                    className="form-control mb-3"
                    value={workoutData.description}
                    onChange={handleChange}
                />
                <button type="submit" className="btn btn-success w-100">Cadastrar Treino</button>
            </form>
        </div>
    );
};

export default TreinoCadastroPage;
