import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, Button, Spinner, Alert, Form } from 'react-bootstrap';

const WorkoutListPage = () => {
  const [workouts, setWorkouts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [trainerId, setTrainerId] = useState(null);  // ID do treinador
  const [selectedStudentId, setSelectedStudentId] = useState(null);  // ID do aluno selecionado
  const [students, setStudents] = useState([]); // Lista de alunos
  const [studentLoading, setStudentLoading] = useState(false); // Carregando alunos

  // Função para buscar os alunos vinculados ao treinador
  const fetchStudentsForTrainer = async () => {
    setStudentLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/trainers/${trainerId}/students`);
      setStudents(response.data); // Atualiza a lista de alunos
    } catch (error) {
      console.error('Erro ao buscar alunos do treinador', error);
      setError('Erro ao carregar os alunos. Tente novamente mais tarde.');
    } finally {
      setStudentLoading(false);
    }
  };

  // Função para buscar os treinos do aluno selecionado
  const fetchWorkoutsForStudent = async (studentId) => {
    if (!studentId) return;

    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/workouts/${studentId}`);
      setWorkouts(response.data); // Atualiza os treinos do aluno
    } catch (error) {
      console.error('Erro ao carregar os treinos', error);
      setError('Erro ao carregar os treinos. Tente novamente mais tarde.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (trainerId) {
      fetchStudentsForTrainer();
    }
  }, [trainerId]); // Carregar alunos sempre que o treinador for selecionado

  useEffect(() => {
    if (selectedStudentId) {
      fetchWorkoutsForStudent(selectedStudentId);
    }
  }, [selectedStudentId]); // Carregar treinos quando o aluno for selecionado

  if (studentLoading) {
    return (
      <div className="text-center">
        <Spinner animation="border" variant="success" />
        <p>Carregando alunos...</p>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="text-center">
        <Spinner animation="border" variant="success" />
        <p>Carregando treinos...</p>
      </div>
    );
  }

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Selecione o Aluno e Veja os Treinos</h1>

      {error && (
        <Alert variant="danger">
          {error}
        </Alert>
      )}

      {/* Seleção de treinador */}
      <Form.Group controlId="trainerSelect">
        <Form.Label>Selecione o Treinador</Form.Label>
        <Form.Control
          as="select"
          value={trainerId}
          onChange={(e) => setTrainerId(e.target.value)}
        >
          <option value="">Selecione o Treinador</option>
          {/* Aqui você deve adicionar os treinadores. Para o exemplo, estamos usando um mock */}
          <option value="1">Treinador 1</option>
          <option value="2">Treinador 2</option>
        </Form.Control>
      </Form.Group>

      {/* Seleção de aluno */}
      {trainerId && (
        <Form.Group controlId="studentSelect">
          <Form.Label>Selecione o Aluno</Form.Label>
          <Form.Control
            as="select"
            value={selectedStudentId || ''}
            onChange={(e) => setSelectedStudentId(e.target.value)}
            disabled={students.length === 0}
          >
            <option value="">Selecione o Aluno</option>
            {students.map((student) => (
              <option key={student.id} value={student.id}>
                {student.name}
              </option>
            ))}
          </Form.Control>
        </Form.Group>
      )}

      {/* Exibindo os treinos */}
      {workouts.length === 0 ? (
        <Alert variant="info" className="text-center">
          Nenhum treino encontrado para o aluno com ID {selectedStudentId}.
        </Alert>
      ) : (
        <div className="row">
          {workouts.map((workout) => (
            <div className="col-md-4 mb-4" key={workout.id}>
              <Card>
                <Card.Body>
                  <Card.Title>{workout.name}</Card.Title>
                  <Card.Subtitle className="mb-2 text-muted">Duração: {workout.duration} minutos</Card.Subtitle>
                  <Card.Text>{workout.description}</Card.Text>
                  <Card.Text>
                    <strong>Treinador:</strong> {workout.trainer_name}
                  </Card.Text>
                  <Button variant="primary" onClick={() => alert('Visualizando mais detalhes do treino!')}>
                    Ver Detalhes
                  </Button>
                </Card.Body>
              </Card>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default WorkoutListPage;
