import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const TreinoCadastroPage = () => {
    const navigate = useNavigate();
    const [workoutData, setWorkoutData] = useState({
        name: '',
        description: '',
        duration: '',
        trainerId: '',
        studentId: '',
    });
    const [exercises, setExercises] = useState([{ id: '', name: '', sets: '', reps: '', weight: '' }]);
    const [feedback, setFeedback] = useState(null);
    const [availableTrainers, setAvailableTrainers] = useState([]); // Lista de treinadores disponíveis

    // Função para tratar mudanças de input
    const handleChange = (e, index) => {
        const { name, value } = e.target;
        if (index !== undefined) {
            const updatedExercises = [...exercises];
            updatedExercises[index][name] = value;
            setExercises(updatedExercises);
        } else {
            setWorkoutData({ ...workoutData, [name]: value });
        }
    };

    // Função para adicionar um novo exercício
    const handleAddExercise = () => {
        setExercises([...exercises, { id: '', name: '', sets: '', reps: '', weight: '' }]);
    };

    // Função para remover um exercício
    const handleRemoveExercise = (index) => {
        const updatedExercises = [...exercises];
        updatedExercises.splice(index, 1);
        setExercises(updatedExercises);
    };

    // Função de envio do formulário
    const handleSubmit = async (e) => {
        e.preventDefault();

        const dataToSend = {
            nome: workoutData.name,
            descricao: workoutData.description,
            duracao: parseInt(workoutData.duration, 10),
            treinador_id: workoutData.trainerId,
            aluno_id: workoutData.studentId,
            exercicios: exercises.map((ex) => ({
                id: ex.id,
                series: parseInt(ex.sets, 10),
                repeticoes: parseInt(ex.reps, 10),
                peso: parseFloat(ex.weight),
            })),
        };

        try {
            const response = await fetch('http://localhost:5000/api/workouts', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(dataToSend),
            });

            if (!response.ok) {
                const errorData = await response.json();
                setFeedback({
                    type: 'danger',
                    message: `Erro: ${errorData.error || 'Erro desconhecido'}`,
                });
            } else {
                setFeedback({ type: 'success', message: 'Treino cadastrado com sucesso!' });
                setTimeout(() => navigate('/treinos'), 1500); // Redirecionar após 1,5 segundos
            }
        } catch (error) {
            setFeedback({ type: 'danger', message: 'Erro ao enviar dados' });
        }
    };

    return (
        <div className="container">
            <h1>Cadastrar Treino</h1>
            {feedback && (
                <div className={`alert alert-${feedback.type}`} role="alert">
                    {feedback.message}
                </div>
            )}
            <form onSubmit={handleSubmit}>
                <div className="form-group mb-3">
                    <label>Nome do Treino:</label>
                    <input
                        type="text"
                        className="form-control"
                        name="name"
                        value={workoutData.name}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div className="form-group mb-3">
                    <label>Descrição:</label>
                    <textarea
                        className="form-control"
                        name="description"
                        value={workoutData.description}
                        onChange={handleChange}
                    />
                </div>

                <div className="form-group mb-3">
                    <label>Duração (em minutos):</label>
                    <input
                        type="number"
                        className="form-control"
                        name="duration"
                        value={workoutData.duration}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div className="form-group mb-3">
                    <label>Selecione um Treinador:</label>
                    <select
                        className="form-control"
                        name="trainerId"
                        value={workoutData.trainerId}
                        onChange={handleChange}
                        required
                    >
                        <option value="">Selecione um Treinador</option>
                        {availableTrainers.map(trainer => (
                            <option key={trainer.id} value={trainer.id}>
                                {trainer.name}
                            </option>
                        ))}
                    </select>
                </div>

                <div className="form-group mb-3">
                    <label>Selecione um Aluno:</label>
                    <select
                        className="form-control"
                        name="studentId"
                        value={workoutData.studentId}
                        onChange={handleChange}
                        required
                    >
                        <option value="">Selecione um Aluno</option>
                        {/* Aqui você pode mapear alunos de forma similar, se necessário */}
                    </select>
                </div>

                <div className="form-group mb-3">
                    <h3>Exercícios</h3>
                    {exercises.map((exercise, index) => (
                        <div key={index} className="mb-3 d-flex gap-3">
                            <input
                                type="text"
                                className="form-control"
                                name="name"
                                placeholder="Nome do exercício"
                                value={exercise.name}
                                onChange={(e) => handleChange(e, index)}
                            />
                            <input
                                type="number"
                                className="form-control"
                                name="sets"
                                placeholder="Séries"
                                value={exercise.sets}
                                onChange={(e) => handleChange(e, index)}
                            />
                            <input
                                type="number"
                                className="form-control"
                                name="reps"
                                placeholder="Repetições"
                                value={exercise.reps}
                                onChange={(e) => handleChange(e, index)}
                            />
                            <input
                                type="number"
                                className="form-control"
                                name="weight"
                                placeholder="Peso"
                                value={exercise.weight}
                                onChange={(e) => handleChange(e, index)}
                            />
                            <button
                                type="button"
                                className="btn btn-danger"
                                onClick={() => handleRemoveExercise(index)}
                            >
                                Remover
                            </button>
                        </div>
                    ))}
                    <button type="button" className="btn btn-secondary" onClick={handleAddExercise}>
                        Adicionar Exercício
                    </button>
                </div>

                <button type="submit" className="btn btn-primary">Salvar Treino</button>
            </form>
        </div>
    );
};

export default TreinoCadastroPage;
