import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Certifique-se de que o Bootstrap esteja importado

const LoginPage = () => {
  const [credentials, setCredentials] = useState({ email: '', password: '' });

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Aqui você pode adicionar a lógica para enviar as credenciais para o backend
    if (credentials.email === 'admin@example.com' && credentials.password === 'senha123') {
      alert('Login bem-sucedido');
      window.location.href = '/users'; // Redireciona para a lista de usuários após o login
    } else {
      alert('Credenciais inválidas');
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Login</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group mb-3">
          <input
            type="email"
            className="form-control"
            name="email"
            placeholder="Email"
            value={credentials.email}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group mb-3">
          <input
            type="password"
            className="form-control"
            name="password"
            placeholder="Senha"
            value={credentials.password}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-success w-100">Entrar</button>
      </form>
    </div>
  );
};

export default LoginPage;
