import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Alert, Spinner } from 'react-bootstrap';
import axios from 'axios';

const WorkoutListPage = () => {
  const [workouts, setWorkouts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [feedback, setFeedback] = useState({ type: '', message: '' });

  useEffect(() => {
    const fetchWorkouts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/workouts');
        setWorkouts(response.data);
      } catch (error) {
        console.error('Erro ao carregar os workouts', error);
        setFeedback({ type: 'danger', message: 'Erro ao carregar os workouts' });
      } finally {
        setLoading(false);
      }
    };

    fetchWorkouts();
  }, []);

  if (loading) {
    return (
      <div className="text-center">
        <Spinner animation="border" variant="success" />
      </div>
    );
  }

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4 text-success">Lista de Workouts</h1>
      
      {/* Feedback de erro ou sucesso */}
      {feedback.message && (
        <Alert variant={feedback.type} onClose={() => setFeedback({ type: '', message: '' })} dismissible>
          {feedback.message}
        </Alert>
      )}

      <div className="list-group">
        {workouts.length === 0 ? (
          <div className="alert alert-warning text-center">Nenhum workout encontrado</div>
        ) : (
          workouts.map((workout) => (
            <div key={workout.id} className="list-group-item list-group-item-action">
              <h5 className="mb-1 text-primary">{workout.name}</h5>
              <p className="mb-1">{workout.description}</p>
              <small>Trainer ID: {workout.trainer_id}</small><br />
              <small>Duração: {workout.duration} minutos</small>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default WorkoutListPage;
