import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    // Verificar campos vazios
    if (!email || !password) {
      alert('Preencha todos os campos!');
      return;
    }

    try {
      // Enviar requisição para o back-end
      const response = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      const result = await response.json();

      // Logando informações para depuração
      console.log('Resposta da API:', result);

      if (response.status === 200) {
        // Se o login for bem-sucedido, navega para a página inicial ou dashboard
        alert('Login bem-sucedido!');
        navigate('/dashboard'); // Altere para o caminho correto
      } else {
        alert(result.message || 'Erro ao fazer login');
      }
    } catch (error) {
      console.error('Erro ao fazer login:', error);
      alert('Erro ao se conectar com o servidor!');
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4 text-primary">Login</h2>
      <form onSubmit={handleLogin} className="bg-light p-4 rounded shadow-sm">
        <div className="mb-3">
          <label htmlFor="email" className="form-label">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="form-control"
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="password" className="form-label">Senha:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="form-control"
            required
          />
        </div>
        <button type="submit" className="btn btn-primary w-100 mt-3">Entrar</button>
      </form>
    </div>
  );
};

export default LoginPage;
