import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const WorkoutListPage = () => {
    const { alunoId } = useParams();
    const [workouts, setWorkouts] = useState([]); // Atualiza para uma lista de treinos
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        if (!alunoId) return;

        const fetchWorkouts = async () => {
            setLoading(true);
            try {
                const response = await axios.get(`http://localhost:5000/api/workouts/user/${alunoId}`);
                setWorkouts(response.data); // Atualiza com a lista de treinos
            } catch (err) {
                setError(err);
            } finally {
                setLoading(false);
            }
        };

        fetchWorkouts();
    }, [alunoId]);

    if (loading) return <div>Carregando treino...</div>;
    if (error) return <div>Erro ao carregar treino: {error.message}</div>;
    if (workouts.length === 0) return <div>Nenhum treino encontrado para o aluno com ID {alunoId}</div>;

    return (
        <div className="container mt-5">
            <h2 className="text-center text-primary">Acompanhamento de Treino</h2>
            {workouts.map((workout) => (
                <div key={workout.id}>
                    <h3>{workout.name}</h3>
                    <p>{workout.description}</p>
                    <p>Duração: {workout.duration} minutos</p>

                    <h4>Exercícios</h4>
                    {workout.exercises.map((exercise, index) => (
                        <div key={index} className="exercise-detail mb-3">
                            <p><strong>Nome:</strong> {exercise.name}</p>
                            <p><strong>Séries:</strong> {exercise.sets}</p>
                            <p><strong>Repetições:</strong> {exercise.reps}</p>
                            <p><strong>Peso:</strong> {exercise.weight} kg</p>
                        </div>
                    ))}
                </div>
            ))}
        </div>
    );
};

export default WorkoutListPage;
