import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Modal, Button, Alert } from 'react-bootstrap'; // Importando Alert para feedbacks

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [feedback, setFeedback] = useState({ type: '', message: '' }); // Feedback para exclusão

  // Mock para exibição inicial
  const mockUsers = [
    { id: 1, name: 'João Silva', email: 'joao@example.com' },
    { id: 2, name: 'Maria Oliveira', email: 'maria@example.com' },
    { id: 3, name: 'Carlos Santos', email: 'carlos@example.com' },
  ];

  useEffect(() => {
    const fetchUsers = () => {
      setUsers(mockUsers);
    };
    fetchUsers();
  }, []);

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/users/${id}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        setUsers(users.filter(user => user.id !== id)); // Remove o usuário da lista localmente
        setFeedback({ type: 'success', message: 'Usuário excluído com sucesso!' });
      } else {
        setFeedback({ type: 'danger', message: 'Erro ao excluir usuário.' });
      }
    } catch (error) {
      console.error(error);
      setFeedback({ type: 'danger', message: 'Erro ao excluir usuário. Tente novamente mais tarde.' });
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Lista de Usuários</h1>

      {/* Feedback de exclusão */}
      {feedback.message && (
        <Alert variant={feedback.type} onClose={() => setFeedback({ type: '', message: '' })} dismissible>
          {feedback.message}
        </Alert>
      )}

      <table className="table table-bordered table-striped">
        <thead className="thead-dark">
          <tr>
            <th>ID</th>
            <th>Nome</th>
            <th>Email</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {users.length === 0 ? (
            <tr>
              <td colSpan="4" className="text-center">
                Nenhum usuário encontrado
              </td>
            </tr>
          ) : (
            users.map(user => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>
                  <button className="btn btn-info btn-sm mr-2">Editar</button>
                  <button className="btn btn-danger btn-sm" onClick={() => handleDelete(user.id)}>
                    Excluir
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default UserList;
