import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Alert } from 'react-bootstrap';
import axios from 'axios';

const TreinoCadastroPage = () => {
    const [workoutData, setWorkoutData] = useState({ name: '', description: '', duration: '' });
    const [exercises, setExercises] = useState([{ id: '', sets: '', reps: '' }]);
    const [availableExercises, setAvailableExercises] = useState([]);  // Lista de exercícios disponíveis
    const [feedback, setFeedback] = useState({ type: '', message: '' });
    const navigate = useNavigate();

    // Carregar a lista de exercícios disponíveis
    useEffect(() => {
        axios.get('http://localhost:5000/api/exercises')
            .then(response => {
                setAvailableExercises(response.data);  // Preencher a lista de exercícios
            })
            .catch(error => {
                console.error('Erro ao carregar exercícios:', error);
            });
    }, []);

    const handleChange = (e) => {
        setWorkoutData({ ...workoutData, [e.target.name]: e.target.value });
    };

    const handleExerciseChange = (index, e) => {
        const updatedExercises = [...exercises];
        updatedExercises[index][e.target.name] = e.target.value;
        setExercises(updatedExercises);
    };

    const addExercise = () => {
        setExercises([...exercises, { id: '', sets: '', reps: '' }]);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const dataToSend = {
            name: workoutData.name,
            description: workoutData.description,
            duration: parseInt(workoutData.duration, 10), // Duração do treino
            exercises: exercises.map(ex => ({
                id: ex.id,
                sets: parseInt(ex.sets, 10),
                reps: parseInt(ex.reps, 10),
            }))
        };

        console.log("Dados enviados:", dataToSend);

        try {
            const response = await fetch('http://localhost:5000/api/workouts', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(dataToSend),
            });

            if (!response.ok) {
                const errorData = await response.json();
                console.error("Erro na resposta do servidor:", errorData);
                setFeedback({ type: 'danger', message: `Erro: ${errorData.error || 'Erro desconhecido'}` });
            } else {
                const result = await response.json();
                console.log("Treino cadastrado com sucesso:", result);
                setFeedback({ type: 'success', message: 'Treino cadastrado com sucesso!' });
                navigate('/treinos');
            }
        } catch (error) {
            console.error("Erro ao enviar dados:", error);
            setFeedback({ type: 'danger', message: 'Erro ao enviar dados' });
        }
    };

    return (
        <div className="container mt-5">
            <h1 className="text-center text-success">Cadastro de Treino</h1>

            {feedback.message && (
                <Alert variant={feedback.type} onClose={() => setFeedback({ type: '', message: '' })} dismissible>
                    {feedback.message}
                </Alert>
            )}

            <form onSubmit={handleSubmit} className="form-group">
                <input
                    type="text"
                    name="name"
                    placeholder="Nome do Treino"
                    className="form-control mb-3"
                    value={workoutData.name}
                    onChange={handleChange}
                    required
                />
                <textarea
                    name="description"
                    placeholder="Descrição"
                    className="form-control mb-3"
                    value={workoutData.description}
                    onChange={handleChange}
                />

                <input
                    type="number"
                    name="duration"
                    placeholder="Duração (minutos)"
                    className="form-control mb-3"
                    value={workoutData.duration}
                    onChange={handleChange}
                    required
                />

                <h5 className="mt-4">Exercícios</h5>
                {exercises.map((exercise, index) => (
                    <div key={index} className="exercise-group mb-3">
                        <select
                            name="id"
                            className="form-control mb-2"
                            value={exercise.id}
                            onChange={(e) => handleExerciseChange(index, e)}
                            required
                        >
                            <option value="">Selecione um exercício</option>
                            {availableExercises.map((availableExercise) => (
                                <option key={availableExercise.id} value={availableExercise.id}>
                                    {availableExercise.name}
                                </option>
                            ))}
                        </select>

                        <input
                            type="number"
                            name="sets"
                            placeholder="Séries"
                            className="form-control mb-2"
                            value={exercise.sets}
                            onChange={(e) => handleExerciseChange(index, e)}
                            required
                        />
                        <input
                            type="number"
                            name="reps"
                            placeholder="Repetições"
                            className="form-control mb-2"
                            value={exercise.reps}
                            onChange={(e) => handleExerciseChange(index, e)}
                            required
                        />
                    </div>
                ))}

                <button type="button" className="btn btn-secondary mb-3" onClick={addExercise}>
                    Adicionar Exercício
                </button>

                <button type="submit" className="btn btn-success w-100">Cadastrar Treino</button>
            </form>
        </div>
    );
};

export default TreinoCadastroPage;
