import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Alert, Button, Modal, Form } from 'react-bootstrap';

const WorkoutListPage = () => {
  const [workouts, setWorkouts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [currentWorkout, setCurrentWorkout] = useState(null);

  useEffect(() => {
    const fetchWorkouts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/workouts');
        setWorkouts(response.data);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    };

    fetchWorkouts();
  }, []);

  const handleEdit = (workout) => {
    setCurrentWorkout(workout);
    setShowEditModal(true);
  };

  const handleDelete = async (id) => {
    console.log('Deleting workout with ID:', id); // Log de depuração
    try {
      await axios.delete(`http://localhost:5000/api/workouts/${id}`);
      setWorkouts(workouts.filter(workout => workout.id !== id));
    } catch (err) {
      console.error('Erro ao excluir treino:', err);
      setError(err);
    }
  };

  const handleUpdate = async () => {
    console.log('Updating workout:', currentWorkout); // Log de depuração
    try {
      const response = await axios.put(`http://localhost:5000/api/workouts/${currentWorkout.id}`, currentWorkout);
      setWorkouts(workouts.map(workout => (workout.id === currentWorkout.id ? response.data : workout)));
      setShowEditModal(false);
      setCurrentWorkout(null);
    } catch (err) {
      console.error('Erro ao atualizar treino:', err);
      setError(err);
    }
  };

  if (loading) return <div>Carregando...</div>;
  if (error) return <Alert variant="danger">Erro ao carregar treinos: {error.message}</Alert>;

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4 text-success">Lista de Treinos</h2>
      <table className="table table-bordered table-striped">
        <thead className="thead-light bg-success text-white">
          <tr>
            <th>ID</th>
            <th>Nome do Treino</th>
            <th>Duração (min)</th>
            <th>Descrição</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {workouts.length === 0 ? (
            <tr>
              <td colSpan="5" className="text-center">Nenhum treino encontrado</td>
            </tr>
          ) : (
            workouts.map((workout) => (
              <tr key={workout.id}>
                <td>{workout.id}</td>
                <td>{workout.name}</td>
                <td>{workout.duration}</td>
                <td>{workout.description}</td>
                <td>
                  <Button variant="warning" onClick={() => handleEdit(workout)}>Editar</Button>
                  <Button variant="danger" onClick={() => handleDelete(workout.id)}>Excluir</Button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      {/* Modal de Edição */}
      <Modal show={showEditModal} onHide={() => setShowEditModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Editar Treino</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="formWorkoutName">
              <Form.Label>Nome do Treino</Form.Label>
              <Form.Control
                type="text"
                value={currentWorkout?.name || ''}
                onChange={(e) => setCurrentWorkout({ ...currentWorkout, name: e.target.value })}
              />
            </Form.Group>
            <Form.Group controlId="formWorkoutDuration">
              <Form.Label>Duração (min)</Form.Label>
              <Form.Control
                type="number"
                value={currentWorkout?.duration || ''}
                onChange={(e) => setCurrentWorkout({ ...currentWorkout, duration: e.target.value })}
              />
            </Form.Group>
            <Form.Group controlId="formWorkoutDescription">
              <Form.Label>Descrição</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={currentWorkout?.description || ''}
                onChange={(e) => setCurrentWorkout({ ...currentWorkout, description: e.target.value })}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowEditModal(false)}>Cancelar</Button>
          <Button variant="primary" onClick={handleUpdate}>Salvar Alterações</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default WorkoutListPage;
