import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Alert, Button } from 'react-bootstrap'; // Importando Alert e Button para feedback

const ListTrainers = () => {
  const [trainers, setTrainers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTrainers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/trainers');
        setTrainers(response.data);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    };

    fetchTrainers();
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/trainers/${id}`);
      setTrainers(trainers.filter(trainer => trainer.id !== id));
    } catch (err) {
      setError(err);
    }
  };

  if (loading) return <div>Carregando...</div>;
  if (error) return <Alert variant="danger">Erro ao carregar treinadores: {error.message}</Alert>;

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4 text-success">Lista de Treinadores</h2>
      <table className="table table-bordered table-striped">
        <thead className="thead-light bg-success text-white">
          <tr>
            <th>ID</th>
            <th>Nome</th>
            <th>Especialidade</th>
            <th>Email</th>
            <th>Ações</th> {/* Adicionado cabeçalho para Ações */}
          </tr>
        </thead>
        <tbody>
          {trainers.length === 0 ? (
            <tr>
              <td colSpan="5" className="text-center"> {/* Atualizado colspan para incluir Ações */}
                Nenhum treinador encontrado
              </td>
            </tr>
          ) : (
            trainers.map((trainer) => (
              <tr key={trainer.id}>
                <td>{trainer.id}</td>
                <td>{trainer.name}</td>
                <td>{trainer.specialty}</td>
                <td>{trainer.email}</td>
                <td>
                  <Button variant="warning" onClick={() => {/* Função para editar */}}>Editar</Button>
                  <Button variant="danger" onClick={() => handleDelete(trainer.id)}>Excluir</Button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ListTrainers;
