import React, { useState, useEffect } from 'react';
import axios from 'axios';

const WorkoutListPage = () => {
  const [workouts, setWorkouts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchWorkouts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/workouts'); // URL do backend onde os workouts estão sendo fornecidos
        console.log('Workouts recebidos:', response.data); // Verificando os dados retornados
        setWorkouts(response.data);
      } catch (error) {
        console.error('Erro ao carregar os workouts', error);
      } finally {
        setLoading(false);
      }
    };

    fetchWorkouts();
  }, []);

  if (loading) {
    return <div>Carregando...</div>;
  }

  return (
    <div>
      <h1>Lista de Workouts</h1>
      <ul>
        {workouts.map((workout) => (
          <li key={workout.id}>
            <h2>{workout.name}</h2> {/* Corrigido para 'name' */}
            <p>{workout.description}</p> {/* Corrigido para 'description' */}
            <p>Trainer ID: {workout.trainer_id}</p> {/* Adicionado para mostrar o ID do trainer */}
            <p>Duração: {workout.duration} minutos</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default WorkoutListPage;
