import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios'; // Importando axios para fazer requisição

const LoginPage = () => {
  const [credentials, setCredentials] = useState({ email: '', password: '' });
  const [errorMessage, setErrorMessage] = useState('');

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Fazendo a requisição para o servidor para validar as credenciais
      const response = await axios.post('http://localhost:5000/api/login', credentials);
      
      // Se a resposta for positiva, o login foi bem-sucedido
      if (response.status === 200) {
        alert('Login bem-sucedido');
        window.location.href = '/users'; // Redireciona para a página de usuários
      }
    } catch (error) {
      // Caso haja erro, mostramos uma mensagem de erro
      setErrorMessage('Credenciais inválidas');
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4 text-success">Login</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group mb-3">
          <input
            type="email"
            className="form-control"
            name="email"
            placeholder="Email"
            value={credentials.email}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group mb-3">
          <input
            type="password"
            className="form-control"
            name="password"
            placeholder="Senha"
            value={credentials.password}
            onChange={handleChange}
            required
          />
        </div>
        {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}
        <button type="submit" className="btn btn-success w-100">Entrar</button>
      </form>
    </div>
  );
};

export default LoginPage;
