import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Certifique-se de que o Bootstrap esteja importado
import { Modal, Button } from 'react-bootstrap'; // Importando componentes do Bootstrap para o modal

const UserList = () => {
  const mockUsers = [
    { id: 1, name: 'João Silva', email: 'joao@example.com' },
    { id: 2, name: 'Maria Oliveira', email: 'maria@example.com' },
    { id: 3, name: 'Carlos Santos', email: 'carlos@example.com' },
  ];

  const [users, setUsers] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    const fetchUsers = () => {
      setUsers(mockUsers);
    };

    fetchUsers();
  }, []);

  const handleShow = (user) => {
    setCurrentUser(user);
    setShowModal(true);
  };

  const handleClose = () => {
    setShowModal(false);
    setCurrentUser(null);
  };

  const handleDelete = (id) => {
    const updatedUsers = users.filter(user => user.id !== id);
    setUsers(updatedUsers);
  };

  const handleSave = () => {
    const updatedUsers = users.map(user => {
      if (user.id === currentUser.id) {
        return currentUser; // Atualiza o usuário editado
      }
      return user;
    });
    setUsers(updatedUsers);
    handleClose(); // Fecha o modal
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Lista de Usuários</h1>
      <table className="table table-bordered table-striped">
        <thead className="thead-dark">
          <tr>
            <th>ID</th>
            <th>Nome</th>
            <th>Email</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {users.length === 0 ? (
            <tr>
              <td colSpan="4" className="text-center">
                Nenhum usuário encontrado
              </td>
            </tr>
          ) : (
            users.map(user => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>
                  <button className="btn btn-info btn-sm mr-2" onClick={() => handleShow(user)}>Editar</button>
                  <button className="btn btn-danger btn-sm" onClick={() => handleDelete(user.id)}>Excluir</button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      {/* Modal de Edição */}
      <Modal show={showModal} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Editar Usuário</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {currentUser && (
            <div>
              <div className="mb-3">
                <label htmlFor="name" className="form-label">Nome</label>
                <input
                  type="text"
                  id="name"
                  className="form-control"
                  value={currentUser.name}
                  onChange={(e) => setCurrentUser({ ...currentUser, name: e.target.value })}
                />
              </div>
              <div className="mb-3">
                <label htmlFor="email" className="form-label">Email</label>
                <input
                  type="email"
                  id="email"
                  className="form-control"
                  value={currentUser.email}
                  onChange={(e) => setCurrentUser({ ...currentUser, email: e.target.value })}
                />
              </div>
            </div>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Fechar
          </Button>
          <Button variant="primary" onClick={handleSave}>
            Salvar
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default UserList;
