import React from 'react';
import { useNavigate } from 'react-router-dom';

const LogoutButton = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    // Limpando o estado de autenticação (remover o usuário do localStorage, por exemplo)
    localStorage.removeItem('user'); // Supondo que você armazenou o usuário no localStorage

    // Redirecionando o usuário para a página de login
    navigate('/login');
  };

  return (
    <button className="btn btn-danger" onClick={handleLogout}>
      Logout
    </button>
  );
};

export default LogoutButton;
