import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { Alert } from 'react-bootstrap'; // Importando componente de alerta do Bootstrap

const HomePage = () => {
  const [userData, setUserData] = useState({ name: '', email: '', password: '' });
  const [feedback, setFeedback] = useState({ type: '', message: '' }); // Estado para armazenar o feedback
  const navigate = useNavigate();

  const handleChange = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/users', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      if (response.ok) {
        setFeedback({ type: 'success', message: 'Usuário criado com sucesso!' });
        setTimeout(() => {
          navigate('/users'); // Redireciona para página de usuários após sucesso
        }, 2000);
      } else {
        setFeedback({ type: 'danger', message: 'Erro ao criar usuário. Verifique os dados e tente novamente.' });
      }
    } catch (error) {
      console.error(error);
      setFeedback({ type: 'danger', message: 'Erro ao criar usuário. Tente novamente mais tarde.' });
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center">Cadastro de Usuário</h1>

      {/* Feedback de sucesso ou erro */}
      {feedback.message && (
        <Alert variant={feedback.type} onClose={() => setFeedback({ type: '', message: '' })} dismissible>
          {feedback.message}
        </Alert>
      )}

      <form onSubmit={handleSubmit} className="form-group">
        <input
          type="text"
          name="name"
          placeholder="Nome"
          className="form-control"
          value={userData.name}
          onChange={handleChange}
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          className="form-control"
          value={userData.email}
          onChange={handleChange}
        />
        <input
          type="password"
          name="password"
          placeholder="Senha"
          className="form-control"
          value={userData.password}
          onChange={handleChange}
        />
        <button type="submit" className="btn btn-success mt-3">Cadastrar</button>
      </form>

      <div className="mt-3">
        <p>Já possui uma conta? <Link to="/login">Entre aqui</Link></p>
      </div>
    </div>
  );
};

export default HomePage;
