import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ListTrainers = () => {
  const [trainers, setTrainers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTrainers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/trainers');
        setTrainers(response.data);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    };

    fetchTrainers();
  }, []);

  if (loading) return <div>Carregando...</div>;
  if (error) return <div>Erro ao carregar treinadores: {error.message}</div>;

  return (
    <div>
      <h2>Lista de Treinadores</h2>
      <ul>
        {trainers.map((trainer) => (
          <li key={trainer.id}>
            {trainer.name} - {trainer.specialty} - {trainer.email}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ListTrainers;
