import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Alert } from 'react-bootstrap';
import axios from 'axios';

const TreinoCadastroPage = () => {
    const [workoutData, setWorkoutData] = useState({ name: '', description: '', duration: '', trainerId: '', studentId: '' });
    const [exercises, setExercises] = useState([{ id: '', sets: '', reps: '', weight: '' }]);
    const [availableExercises, setAvailableExercises] = useState([]);
    const [availableTrainers, setAvailableTrainers] = useState([]);
    const [availableStudents, setAvailableStudents] = useState([]);
    const [feedback, setFeedback] = useState({ type: '', message: '' });
    const navigate = useNavigate();

    useEffect(() => {
        axios.get('http://localhost:5000/api/exercises')
            .then(response => {
                setAvailableExercises(response.data);
            })
            .catch(error => {
                console.error('Erro ao carregar exercÃ­cios:', error);
            });

        // Carregar treinadores e alunos
        axios.get('http://localhost:5000/api/trainers')
            .then(response => {
                setAvailableTrainers(response.data);
            })
            .catch(error => {
                console.error('Erro ao carregar treinadores:', error);
            });

        // Carregar apenas alunos com user_type "aluno"
        axios.get('http://localhost:5000/api/students')
            .then(response => {
                const filteredStudents = response.data.filter(student => student.user_type === 'aluno');
                setAvailableStudents(filteredStudents);
            })
            .catch(error => {
                console.error('Erro ao carregar alunos:', error);
            });
    }, []);

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const response = await axios.post('http://localhost:5000/api/workouts', {
                name: workoutData.name,
                description: workoutData.description,
                duration: workoutData.duration,
                user_id: workoutData.studentId, // ID do aluno
                exercises: exercises,
            });
            setFeedback({ type: 'success', message: 'Treino criado com sucesso!' });
            setWorkoutData({ name: '', description: '', duration: '', studentId: '' });
            setExercises([{ id: '', sets: '', reps: '', weight: '' }]);
        } catch (error) {
            setFeedback({ type: 'danger', message: 'Erro ao criar treino' });
            console.error('Erro ao criar treino:', error);
        }
    };

    return (
        <div className="container mt-5">
            <h2 className="text-center text-primary">Cadastro de Treino</h2>
            {feedback.message && (
                <Alert variant={feedback.type} onClose={() => setFeedback({ type: '', message: '' })} dismissible>
                    {feedback.message}
                </Alert>
            )}
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Nome do Treino</label>
                    <input
                        type="text"
                        className="form-control"
                        value={workoutData.name}
                        onChange={(e) => setWorkoutData({ ...workoutData, name: e.target.value })}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>DescriÃ§Ã£o</label>
                    <input
                        type="text"
                        className="form-control"
                        value={workoutData.description}
                        onChange={(e) => setWorkoutData({ ...workoutData, description: e.target.value })}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>DuraÃ§Ã£o (em minutos)</label>
                    <input
                        type="number"
                        className="form-control"
                        value={workoutData.duration}
                        onChange={(e) => setWorkoutData({ ...workoutData, duration: e.target.value })}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Aluno</label>
                    <select
                        className="form-control"
                        value={workoutData.studentId}
                        onChange={(e) => setWorkoutData({ ...workoutData, studentId: e.target.value })}
                        required
                    >
                        <option value="">Selecione um aluno</option>
                        {availableStudents.map((student) => (
                            <option key={student.id} value={student.id}>
                                {student.name}
                            </option>
                        ))}
                    </select>
                </div>
                <div className="form-group">
                    <label>Treinador</label>
                    <select
                        className="form-control"
                        value={workoutData.trainerId}
                        onChange={(e) => setWorkoutData({ ...workoutData, trainerId: e.target.value })}
                        required
                    >
                        <option value="">Selecione um treinador</option>
                        {availableTrainers.map((trainer) => (
                            <option key={trainer.id} value={trainer.id}>
                                {trainer.name}
                            </option>
                        ))}
                    </select>
                </div>

                <div className="form-group mt-3">
                    <h5>ExercÃ­cios</h5>
                    {exercises.map((exercise, index) => (
                        <div key={index} className="form-row">
                            <div className="col-md-6">
                                <select
                                    className="form-control"
                                    value={exercise.id}
                                    onChange={(e) =>
                                        setExercises((prevExercises) =>
                                            prevExercises.map((prevExercise, i) =>
                                                i === index ? { ...prevExercise, id: e.target.value } : prevExercise
                                            )
                                        )
                                    }
                                >
                                    <option value="">Selecione um exercÃ­cio</option>
                                    {availableExercises.map((exercise) => (
                                        <option key={exercise.id} value={exercise.id}>
                                            {exercise.name}
                                        </option>
                                    ))}
                                </select>
                            </div>
                            <div className="col-md-2">
                                <input
                                    type="number"
                                    className="form-control"
                                    placeholder="SÃ©ries"
                                    value={exercise.sets}
                                    onChange={(e) =>
                                        setExercises((prevExercises) =>
                                            prevExercises.map((prevExercise, i) =>
                                                i === index ? { ...prevExercise, sets: e.target.value } : prevExercise
                                            )
                                        )
                                    }
                                />
                            </div>
                            <div className="col-md-2">
                                <input
                                    type="number"
                                    className="form-control"
                                    placeholder="RepetiÃ§Ãµes"
                                    value={exercise.reps}
                                    onChange={(e) =>
                                        setExercises((prevExercises) =>
                                            prevExercises.map((prevExercise, i) =>
                                                i === index ? { ...prevExercise, reps: e.target.value } : prevExercise
                                            )
                                        )
                                    }
                                />
                            </div>
                        </div>
                    ))}
                    <button
                        type="button"
                        className="btn btn-secondary mt-3"
                        onClick={() =>
                            setExercises([...exercises, { id: '', sets: '', reps: '', weight: '' }])
                        }
                    >
                        Adicionar ExercÃ­cio
                    </button>
                </div>

                <button type="submit" className="btn btn-primary mt-4">Salvar Treino</button>
            </form>
        </div>
    );
};

export default TreinoCadastroPage;
