import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const WorkoutListPage = () => {
    const { alunoId } = useParams(); // Obtém o alunoId da URL
    const [workout, setWorkout] = useState(null); // Armazena os treinos do aluno
    const [loading, setLoading] = useState(true); // Controle de carregamento
    const [error, setError] = useState(null); // Controle de erros

    useEffect(() => {
        if (!alunoId) return;  // Se o alunoId não estiver disponível, não faz a requisição

        const fetchWorkout = async () => {
            setLoading(true);
            try {
                const response = await axios.get(`http://localhost:5000/api/workouts/aluno/${alunoId}`);
                setWorkout(response.data); // Atualiza o estado com os dados do treino
            } catch (err) {
                setError(err); // Caso haja erro, armazena no estado
            } finally {
                setLoading(false); // Finaliza o carregamento
            }
        };

        fetchWorkout(); // Chama a função para buscar os treinos do aluno
    }, [alunoId]); // Recarrega quando o alunoId mudar

    if (loading) return <div>Carregando treino...</div>;
    if (error) return <div>Erro ao carregar treino: {error.message}</div>;
    if (!workout) return <div>Treino não encontrado para o aluno com ID {alunoId}</div>;

    return (
        <div className="container mt-5">
            <h2 className="text-center text-primary">Acompanhamento de Treino</h2>
            <h3>{workout.name}</h3>
            <p>{workout.description}</p>
            <p>Duração: {workout.duration} minutos</p>

            <h4>Exercícios</h4>
            {workout.exercises.map((exercise, index) => (
                <div key={index} className="exercise-detail mb-3">
                    <p><strong>Nome:</strong> {exercise.name}</p>
                    <p><strong>Séries:</strong> {exercise.sets}</p>
                    <p><strong>Repetições:</strong> {exercise.reps}</p>
                    <p><strong>Peso:</strong> {exercise.weight} kg</p>
                </div>
            ))}
        </div>
    );
};

export default WorkoutListPage;
