import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Modal, Button, Alert } from 'react-bootstrap';
import LogoutButton from './LogoutButton';
import axios from 'axios';

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [userToDelete, setUserToDelete] = useState(null);
  const [userToEdit, setUserToEdit] = useState({ id: null, name: '', email: '' });
  const [feedback, setFeedback] = useState({ type: '', message: '' });

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/users');
        console.log('Usuários recebidos:', response.data); // Verifique se os dados estão sendo retornados corretamente
        setUsers(response.data);
      } catch (error) {
        console.error('Erro ao buscar usuários:', error);
        setFeedback({ type: 'danger', message: 'Erro ao carregar usuários' });
      }
    };
  
    fetchUsers();
  }, []);

  const handleDelete = (id) => {
    setUserToDelete(id);
    setShowDeleteModal(true);
  };

  const confirmDelete = async () => {
    try {
        await axios.delete(`http://localhost:5000/api/users/${userToDelete}`);
        const updatedUsers = users.filter(user => user.id !== userToDelete);
        setUsers(updatedUsers);
        setShowDeleteModal(false);
        setFeedback({ type: 'success', message: 'Usuário excluído com sucesso!' });
    } catch (error) {
        console.error('Erro ao excluir usuário:', error.response ? error.response.data : error.message);
        setFeedback({ type: 'danger', message: 'Erro ao excluir usuário. ' + (error.response?.data?.error || 'Erro desconhecido.') });
    }
};

  const handleEdit = (user) => {
    setUserToEdit(user);
    setShowEditModal(true);
  };

  const handleUpdate = async () => {
    try {
        const response = await axios.put(`http://localhost:5000/api/users/${userToEdit.id}`, userToEdit);
        const updatedUsers = users.map(user => (user.id === response.data.id ? response.data : user));
        setUsers(updatedUsers);
        setShowEditModal(false);
        setFeedback({ type: 'success', message: 'Usuário atualizado com sucesso!' });
    } catch (error) {
        console.error('Erro ao atualizar usuário:', error.response ? error.response.data : error.message);
        setFeedback({ type: 'danger', message: 'Erro ao atualizar usuário. ' + (error.response?.data?.error || 'Erro desconhecido.') });
    }
};

  const handleCloseDelete = () => {
    setShowDeleteModal(false);
    setUserToDelete(null);
  };

  const handleCloseEdit = () => {
    setShowEditModal(false);
    setUserToEdit({ id: null, name: '', email: '' });
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4 text-success">Lista de Usuários</h1>
      <LogoutButton />
      {feedback.message && (
        <Alert variant={feedback.type} onClose={() => setFeedback({ type: '', message: '' })} dismissible>
          {feedback.message}
        </Alert>
      )}

      <table className="table table-bordered table-striped">
        <thead className="thead-light bg-success text-white">
          <tr>
            <th>ID</th>
            <th>Nome</th>
            <th>Email</th>
            <th>Tipo de Usuário</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {users.length === 0 ? (
            <tr>
              <td colSpan="5" className="text-center">
                Nenhum usuário encontrado
              </td>
            </tr>
          ) : (
            users.map(user => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.user_type}</td> {/* Exibindo o tipo de usuário */}
                <td>
                  <button className="btn btn-warning btn-sm mr-2" onClick={() => handleEdit(user)}>Editar</button>
                  <button
                    className="btn btn-danger btn-sm"
                    onClick={() => handleDelete(user.id)}
                  >
                    Excluir
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      {/* Modal de confirmação de exclusão */}
      <Modal show={showDeleteModal} onHide={handleCloseDelete}>
        <Modal.Header closeButton>
          <Modal.Title>Confirmar Exclusão</Modal.Title>
        </Modal.Header>
        <Modal.Body>Você realmente deseja excluir este usuário?</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseDelete}>Cancelar</Button>
          <Button variant="danger" onClick={confirmDelete}>Excluir</Button>
        </Modal.Footer>
      </Modal>

      {/* Modal de edição */}
      <Modal show={showEditModal} onHide={handleCloseEdit}>
        <Modal.Header closeButton>
          <Modal.Title>Editar Usuário</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form>
            <input
              type="text"
              className="form-control mb-2"
              placeholder="Nome"
              value={userToEdit.name}
              onChange={e => setUserToEdit({ ...userToEdit, name: e.target.value })}
            />
            <input
              type="email"
              className="form-control mb-2"
              placeholder="Email"
              value={userToEdit.email}
              onChange={e => setUserToEdit({ ...userToEdit, email: e.target.value })}
            />
          </form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseEdit}>Cancelar</Button>
          <Button variant="primary" onClick={handleUpdate}>Salvar</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default UserList;
