import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Modal, Button, Alert } from 'react-bootstrap'; // Importando Alert para feedback
import LogoutButton from './LogoutButton'; // Importando o botão de logout
import axios from 'axios'; // Importando axios para fazer requisições HTTP

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [userToDelete, setUserToDelete] = useState(null);
  const [feedback, setFeedback] = useState({ type: '', message: '' }); // Estado para feedback

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get('/api/users'); // Ajuste a URL de acordo com sua configuração
        setUsers(response.data); // Definir os usuários obtidos da API
      } catch (error) {
        console.error('Erro ao buscar usuários:', error);
        setFeedback({ type: 'danger', message: 'Erro ao carregar usuários' });
      }
    };

    fetchUsers();
  }, []);

  const handleDelete = (id) => {
    setUserToDelete(id); // Definir o usuário a ser excluído
    setShowModal(true); // Mostrar o modal de confirmação
  };

  const confirmDelete = async () => {
    try {
      await axios.delete(`/api/users/${userToDelete}`); // Ajuste a URL de acordo com sua configuração
      const updatedUsers = users.filter(user => user.id !== userToDelete); // Atualizar a lista de usuários após a exclusão
      setUsers(updatedUsers); // Atualizar o estado com a nova lista
      setShowModal(false); // Fechar o modal
      setFeedback({ type: 'success', message: 'Usuário excluído com sucesso!' }); // Feedback de sucesso
    } catch (error) {
      console.error('Erro ao excluir usuário:', error);
      setFeedback({ type: 'danger', message: 'Erro ao excluir usuário' }); // Feedback de erro
    }
  };

  const handleClose = () => {
    setShowModal(false); // Fechar o modal sem excluir
    setUserToDelete(null); // Limpar o usuário a ser excluído
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4 text-success">Lista de Usuários</h1>

      {/* Botão de Logout */}
      <LogoutButton />

      {/* Feedback de exclusão */}
      {feedback.message && (
        <Alert variant={feedback.type} onClose={() => setFeedback({ type: '', message: '' })} dismissible>
          {feedback.message}
        </Alert>
      )}

      <table className="table table-bordered table-striped">
        <thead className="thead-light bg-success text-white">
          <tr>
            <th>ID</th>
            <th>Nome</th>
            <th>Email</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {users.length === 0 ? (
            <tr>
              <td colSpan="4" className="text-center">
                Nenhum usuário encontrado
              </td>
            </tr>
          ) : (
            users.map(user => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>
                  <button className="btn btn-warning btn-sm mr-2">Editar</button>
                  <button
                    className="btn btn-danger btn-sm"
                    onClick={() => handleDelete(user.id)} // Ação de exclusão
                  >
                    Excluir
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      {/* Modal de confirmação */}
      <Modal show={showModal} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Confirmar Exclusão</Modal.Title>
        </Modal.Header>
        <Modal.Body>Você realmente deseja excluir este usuário?</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Cancelar
          </Button>
          <Button variant="danger" onClick={confirmDelete}>
            Excluir
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default UserList;
