import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Modal, Button } from 'react-bootstrap';

const UserList = () => {
  const mockUsers = [
    { id: 1, name: 'João Silva', email: 'joao@example.com' },
    { id: 2, name: 'Maria Oliveira', email: 'maria@example.com' },
    { id: 3, name: 'Carlos Santos', email: 'carlos@example.com' },
  ];

  const [users, setUsers] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [userToDelete, setUserToDelete] = useState(null);

  useEffect(() => {
    const fetchUsers = () => {
      setUsers(mockUsers); // Simulação de carregamento de usuários
    };

    fetchUsers();
  }, []);

  const handleDelete = (id) => {
    setUserToDelete(id); // Definir o usuário a ser excluído
    setShowModal(true); // Mostrar o modal de confirmação
  };

  const confirmDelete = () => {
    const updatedUsers = users.filter(user => user.id !== userToDelete); // Atualizar a lista de usuários após a exclusão
    setUsers(updatedUsers); // Atualizar o estado com a nova lista
    setShowModal(false); // Fechar o modal
    alert('Usuário excluído com sucesso!');
  };

  const handleClose = () => {
    setShowModal(false); // Fechar o modal sem excluir
    setUserToDelete(null); // Limpar o usuário a ser excluído
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Lista de Usuários</h1>
      <table className="table table-bordered table-striped">
        <thead className="thead-dark">
          <tr>
            <th>ID</th>
            <th>Nome</th>
            <th>Email</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {users.length === 0 ? (
            <tr>
              <td colSpan="4" className="text-center">
                Nenhum usuário encontrado
              </td>
            </tr>
          ) : (
            users.map(user => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>
                  <button className="btn btn-info btn-sm mr-2">Editar</button>
                  <button
                    className="btn btn-danger btn-sm"
                    onClick={() => handleDelete(user.id)} // Ação de exclusão
                  >
                    Excluir
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      {/* Modal de confirmação */}
      <Modal show={showModal} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Confirmar Exclusão</Modal.Title>
        </Modal.Header>
        <Modal.Body>Você realmente deseja excluir este usuário?</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Cancelar
          </Button>
          <Button variant="danger" onClick={confirmDelete}>
            Excluir
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default UserList;
