import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom'; // Para acessar o ID da URL

const WorkoutListPage = () => {
  const [workouts, setWorkouts] = useState([]);
  const [loading, setLoading] = useState(true);
  const { id } = useParams(); // Pegando o 'id' da URL

  useEffect(() => {
    const fetchWorkouts = async () => {
      try {
        // Alterando a requisição para enviar o user_id como query
        const response = await axios.get(`http://localhost:5000/api/workouts?user_id=${id}`);
        console.log('Workouts recebidos:', response.data);
        setWorkouts(response.data);
      } catch (error) {
        console.error('Erro ao carregar os workouts', error);
      } finally {
        setLoading(false);
      }
    };

    fetchWorkouts();
  }, [id]); // Dependência do id

  if (loading) {
    return <div>Carregando...</div>;
  }

  return (
    <div>
      <h1>Lista de Workouts</h1>
      <ul>
        {workouts.map((workout) => (
          <li key={workout.id}>
            <h2>{workout.name}</h2>
            <p>{workout.description}</p>
            <p>Trainer ID: {workout.trainer_id}</p>
            <p>Duração: {workout.duration} minutos</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default WorkoutListPage;
