import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const WorkoutListPage = () => {
  const [workouts, setWorkouts] = useState([]);
  const [loading, setLoading] = useState(true);
  const { id } = useParams(); // Pegando o 'id' da URL (que é o user_id)

  useEffect(() => {
    const fetchWorkouts = async () => {
      if (!id) {
        console.error('ID não fornecido na URL');
        return;
      }

      const userId = parseInt(id, 10);

      if (isNaN(userId)) {
        console.error('ID inválido');
        return;
      }

      try {
        console.log('Fazendo requisição para treinos...');
        const response = await axios.get(`http://localhost:5000/api/workouts/${userId}`);
        console.log('Resposta recebida:', response.data);

        setWorkouts(response.data);
      } catch (error) {
        console.error('Erro ao carregar os workouts', error);
      } finally {
        setLoading(false);
      }
    };

    fetchWorkouts();
  }, [id]); // Dependência do id

  if (loading) {
    return <div>Carregando...</div>;
  }

  return (
    <div>
      <h1>Lista de Workouts</h1>
      <ul>
        {workouts.length === 0 ? (
          <li>Nenhum treino encontrado para o usuário com ID {id}.</li>
        ) : (
          workouts.map((workout) => (
            <li key={workout.id}>
              <h2>{workout.name}</h2>
              <p>{workout.description}</p>
              <p>Trainer ID: {workout.trainer_id}</p>
              <p>Duração: {workout.duration} minutos</p>
            </li>
          ))
        )}
      </ul>
    </div>
  );
};

export default WorkoutListPage;
