import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { Alert } from 'react-bootstrap';

const HomePage = () => {
  const [userData, setUserData] = useState({ name: '', email: '', password: '', userType: 'aluno' });
  const [feedback, setFeedback] = useState({ type: '', message: '' });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/users', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      if (response.ok) {
        setFeedback({ type: 'success', message: 'Usuário criado com sucesso!' });
        setTimeout(() => {
          navigate('/user-details'); // Redireciona para a página de detalhes do usuário
        }, 2000);
      } else {
        setFeedback({ type: 'danger', message: 'Erro ao criar usuário. Verifique os dados e tente novamente.' });
      }
    } catch (error) {
      console.error(error);
      setFeedback({ type: 'danger', message: 'Erro ao criar usuário. Tente novamente mais tarde.' });
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center text-success">Cadastro de Usuário</h1>

      {feedback.message && (
        <Alert variant={feedback.type} onClose={() => setFeedback({ type: '', message: '' })} dismissible>
          {feedback.message}
        </Alert>
      )}

      <form onSubmit={handleSubmit} className="form-group">
        <input
          type="text"
          name="name"
          placeholder="Nome"
          className="form-control mb-3"
          value={userData.name}
          onChange={handleChange}
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          className="form-control mb-3"
          value={userData.email}
          onChange={handleChange}
        />
        <input
          type="password"
          name="password"
          placeholder="Senha"
          className="form-control mb-3"
          value={userData.password}
          onChange={handleChange}
        />
        <select
          name="userType"
          className="form-control mb-3"
          value={userData.userType}
          onChange={handleChange}
        >
          <option value="aluno">Aluno</option>
          <option value="treinador">Treinador</option>
        </select>
        <button type="submit" className="btn btn-success w-100">Cadastrar</button>
      </form>
      <div className="text-center mt-3">
        <Link to="/login" className="text-decoration-none text-success">Já tem uma conta? Faça login aqui</Link>
      </div>
    </div>
  );
};

export default HomePage;
